<?phpclass mcart extends CI_Model{    protected $_table='carts';    public function __construct(){        parent::__construct();    }    public function listAll($limit,$start,$s=''){        $this->db->select('*');        $this->db->order_by("id", "desc");        $this->db->like("id", $s);        $this->db->where('status !=',-1);        $this->db->limit($limit,$start);        return $this->db->get($this->_table)->result_array();    }    public function add($data){        $this->db->insert($this->_table,$data);        return $this->db->insert_id();    }        public function checkTime(){        $this->db->select('*');        $this->db->where('status',-1);        $cart = $this->db->get($this->_table)->result_array();        $time = time();        for($i=0;$i<count($cart);$i++)            if($time-$cart[$i]['date']>=18000){                $this->del($cart[$i]['id']);            }    }    public function del($id){        $this->db->where('id',$id);        $this->db->delete($this->_table);    }    public function numRows($s=''){        $this->db->like("id", $s);        $this->db->where('status !=',-1);        return $this->db->get($this->_table)->num_rows();    }    public function getById($id){        $this->db->where("id",$id);        return $this->db->get($this->_table)->row_array();    }    public function getById2($id){        $this->db->where("id",$id);        return $this->db->get($this->_table)->result_array();    }    public function editById($id,$data){        $this->db->where("id",$id);        $this->db->update($this->_table,$data);    }    public function getByUser($id){        $this->db->where("user_id",$id);        $this->db->where('status !=',-1);        $this->db->order_by("id", "desc");        return $this->db->get($this->_table)->result_array();    }    public function getByUser_ok($id){        $this->db->where("user_id",$id);        $this->db->where('status !=',-1);        $this->db->order_by("id", "desc");        return $this->db->get($this->_table)->result_array();    }    public function getByIP($id){        $this->db->where("ip",$id);        $this->db->where("status",-1);        $this->db->where("user_id",0);        $this->db->order_by("id", "desc");        return $this->db->get($this->_table)->result_array();    }}