<?phpclass mcategory extends CI_Model{    protected $_table='category';    public function __construct(){        parent::__construct();    }    public function numRows(){        return $this->db->get($this->_table)->num_rows();    }    public function getAll(){        $this->db->select('*');        $this->db->order_by("order", "asc");        return $this->db->get($this->_table)->result_array();    }    public function getAll2(){        $this->db->select('*');        $this->db->order_by("name", "asc");        return $this->db->get($this->_table)->result_array();    }    function getbyparent($id = 0){        $this->db->where('parent_id',$id);        $this->db->order_by("order", "asc");        $query = $this->db->get('category');        return $query->result();    }    function getbyid($id){        $this->db->where('id', $id);        $query = $this->db->get('category');        return $query->row();    }    public function getId($id){        $this->db->where("id",$id);        return $this->db->get($this->_table)->row_array();    }    function getByLink($slug){        $this->db->where('link', $slug);        return $this->db->get($this->_table)->row_array();    }    function getByLink2($slug,$parent){        $this->db->where('link', $slug);        $this->db->where('parent_id', $parent);        return $this->db->get($this->_table)->row_array();    }// function kiá»ƒm tra tá»“n táº¡i user    public function checkLink($link){        $this->db->where("link",$link);        if($this->db->get($this->_table)->num_rows()>0){            return false;        }else{            return true;        }    }    function getguide($id, $trees = NULL)    {        if(!$trees){            $trees = array();        }        $query = $this->getbyid(intval($id));        if (!empty($query)){            array_push($trees,$query);            $trees = $this->getguide($query->parent_id, $trees);        }        return $trees;    }    /*        Get all child category include parent category        Return: array id of category    */    function getfamily($id, $trees = NULL){        if(!$trees){            $trees = array();            array_push($trees,$id);        }        $query = $this->getbyparent(intval($id));        foreach ($query as $row):            array_push($trees,$row->id);            $trees = $this->getfamily($row->id, $trees);        endforeach;        return $trees;    }    function delete($id){        $this->db->where('id', $id);        $this->db->delete('category');    }    function add($data){        $this->db->insert('category',$data);    }    function editById($id,$data){        $this->db->where('id', $id);        $this->db->update('category', $data);    }    /*        Get all child category        Return: array object category    */    function getMenu($parentid = 0,$space = '', $trees = NULL){        if(!$trees) $trees = array();        $query = $this->getbyparent(intval($parentid));        foreach ($query as $row):            $row->name = $space.' '.$row->name;            array_push($trees,$row);            $trees = $this->getmenu($row->id,'---'.$space,$trees);        endforeach;        return $trees;    }    function getUpperCat($id)    {        $data = $this->getguide($id);        $result = array();        foreach($data as $row){            array_push($result, $row->link);        }        return $result;    }    }