<?phpclass mcustomer extends CI_Model{    protected $_table='customers';    public function __construct(){        parent::__construct();    }    public function listAll($limit,$start,$name=''){        $this->db->select('*');        $this->db->limit($limit,$start);        $this->db->order_by('id','desc');        $this->db->like("name",$name);        return $this->db->get($this->_table)->result_array();    }    public function getAll(){        $this->db->select('*');        return $this->db->get($this->_table)->result_array();    }    public function add($data){        $this->db->insert($this->_table,$data);        return $this->db->insert_id();    }    public function del($id){        $this->db->where('id',$id);        $this->db->delete($this->_table);    }    public function numRows($name=''){        $this->db->like("name",$name);        return $this->db->get($this->_table)->num_rows();    }    public function getById($id){        $this->db->where("id",$id);        return $this->db->get($this->_table)->row_array();    }    public function editById($id,$data){        $this->db->where("id",$id);        $this->db->update($this->_table,$data);    }    // function    public function checkName($name){        $this->db->where("name",$name);        if($this->db->get($this->_table)->num_rows()>0){            return false;        }else{            return true;        }    }    public function checkCode($code){        $this->db->where("code",$code);        if($this->db->get($this->_table)->num_rows()>0){            return false;        }else{            return true;        }    }    public function phieuthu($id,$limit,$start){        $this->db->select('phieuthus.*,customers.name');        $this->db->join('customers','phieuthus.customer_id = customers.id');        $this->db->limit($limit,$start);        $this->db->where("phieuthus.customer_id",$id);        $this->db->order_by('id','desc');        return $this->db->get('phieuthus')->result_array();    }    public function phieuthuRows($id){        $this->db->where("customer_id",$id);        return $this->db->get("phieuthus")->num_rows();    }    public function phieuthudv($id,$limit,$start){        $this->db->select('phieuthudvs.*,customers.name');        $this->db->join('customers','phieuthudvs.customer_id = customers.id');        $this->db->limit($limit,$start);        $this->db->where("phieuthudvs.customer_id",$id);        $this->db->order_by('id','desc');        return $this->db->get('phieuthudvs')->result_array();    }    public function phieuthudvRows($id){        $this->db->where("customer_id",$id);        return $this->db->get("phieuthudvs")->num_rows();    }    public function phieuchi($id,$limit,$start){        $this->db->select('phieuchis.*,customers.name');        $this->db->join('customers','phieuchis.customer_id = customers.id');        $this->db->limit($limit,$start);        $this->db->where("phieuchis.customer_id",$id);        $this->db->order_by('id','desc');        return $this->db->get('phieuchis')->result_array();    }    public function phieuchiRows($id){        $this->db->where("customer_id",$id);        return $this->db->get("phieuchis")->num_rows();    }    public function phieubanhang($id,$limit,$start){        $this->db->select('phieubanhang.*,customers.name');        $this->db->join('customers','phieubanhang.customer_id = customers.id');        $this->db->limit($limit,$start);        $this->db->where("phieubanhang.customer_id",$id);        $this->db->order_by('id','desc');        return $this->db->get('phieubanhang')->result_array();    }    public function phieubanhangRows($id){        $this->db->where("customer_id",$id);        return $this->db->get("phieubanhang")->num_rows();    }    public function congno($id,$limit,$start){        $this->db->select('congnos.*,customers.name');        $this->db->join('customers','congnos.customer_id = customers.id');        $this->db->limit($limit,$start);        $this->db->where("congnos.customer_id",$id);        $this->db->order_by('id','desc');        return $this->db->get('congnos')->result_array();    }    public function congnoRows($id){        $this->db->where("customer_id",$id);        return $this->db->get("congnos")->num_rows();    }}