<?phpclass mdetail extends CI_Model{    protected $_table='details';    public function __construct(){        parent::__construct();    }    public function add($data){        $this->db->insert($this->_table,$data);        return $this->db->insert_id();    }    public function del($id){        $this->db->where('id',$id);        $this->db->delete($this->_table);    }    public function getByCartId($id){        $this->db->where("cart_id",$id);        return $this->db->get($this->_table)->result_array();    }    public function editById($id,$data){        $this->db->where("id",$id);        $this->db->update($this->_table,$data);    }    public function getByUser($id){        $this->db->where("user_id",$id);        $this->db->order_by("id", "desc");        return $this->db->get($this->_table)->result_array();    }}