<?phpclass mphieuchi extends CI_Model{    protected $_table='phieuchis';    public function __construct(){        parent::__construct();    }    public function listAll($limit,$start){        $this->db->select('phieuchis.*,customers.name');        $this->db->join('customers','phieuchis.customer_id = customers.id');        $this->db->limit($limit,$start);        $this->db->order_by('id','desc');        return $this->db->get($this->_table)->result_array();    }    public function search($limit,$start,$dstart,$dend,$mapt=''){        $this->db->select("phieuchis.*,customers.name,UNIX_TIMESTAMP('ngay-thang-nam') AS abc");        $this->db->join('customers','phieuchis.customer_id = customers.id');        if($dstart !='' && $dend != ''){            $astart = explode('-',$dstart);            $dstart = $astart[2].'-'.$astart[1].'-'.$astart[0];            $aend = explode('-',$dend);            $dend = $aend[2].'-'.$aend[1].'-'.$aend[0];        $this->db->where("date BETWEEN '$dstart' AND '$dend' ");        }        $this->db->like("mapt",$mapt);        $this->db->limit($limit,$start);        $this->db->order_by('id','desc');        return $this->db->get($this->_table)->result_array();    }    public function getAll(){        $this->db->select('*');        return $this->db->get($this->_table)->result_array();    }    public function add($data){        $this->db->insert($this->_table,$data);    }    public function del($id){        $this->db->where('id',$id);        $this->db->delete($this->_table);    }  public function numRows($dstart='',$dend='',$mapt=''){        $this->db->select("phieuchis.*,customers.name,UNIX_TIMESTAMP('ngay-thang-nam') AS abc");        $this->db->join('customers','phieuchis.customer_id = customers.id');        if($dstart !='' && $dend != ''){            $astart = explode('-',$dstart);            $dstart = $astart[2].'-'.$astart[1].'-'.$astart[0];            $aend = explode('-',$dend);            $dend = $aend[2].'-'.$aend[1].'-'.$aend[0];            $this->db->where("date BETWEEN '$dstart' AND '$dend' ");        }        $this->db->like("mapt",$mapt);        return $this->db->get($this->_table)->num_rows();    }    public function getById($id){        $this->db->where("id",$id);        return $this->db->get($this->_table)->row_array();    }    public function editById($id,$data){        $this->db->where("id",$id);        $this->db->update($this->_table,$data);    }}