<?phpclass coupon extends MY_Controller{	public function __construct(){		parent::__construct();        $this->_data['now'] = 'Mã Giảm Giá';		$this->load->model('mcoupon');        $user = $this->session->userdata('user');        if(!$user || $user['user_type'] != 'administrator'){            if($user['user_type']!='nhanvien' || !strpos($_SERVER['REQUEST_URI'],'admin/product/phieubanhang')){                redirect(base_url()."admin/product/phieubanhang");                }                        }        $this->form_validation->CI =& $this;	}	public function listall(){        if(!isset($_GET['per_page'])){            $start = 0 ;        }else{            $start=$_GET['per_page'];        }        $this->load->library('pagination');		$config['base_url'] = base_url().'admin/coupon/listall?';		//config phân trang		$config['total_rows'] = $this->mcoupon->numRows();		$config['per_page'] =8;		$config['uri_segment'] = 4;		$config['cur_tag_open'] = "<li><a><font color='black'>";		$config['cur_tag_close'] = '</font></a></li>';		$config['num_tag_open'] = '<li>';		$config['num_tag_close'] = '</li>';		$config['prev_link'] = 'Prev';		$config['prev_tag_open'] = '<li>';		$config['prev_tag_close'] = '</li>';		$config['next_link'] = 'Next';		$config['next_tag_open'] = '<li>';		$config['next_tag_close'] = '</li>';		$config['first_tag_open'] = '<li>';		$config['first_tag_close'] = '</li>';		$config['last_tag_open'] = '<li>';		$config['last_tag_close'] = '</li>';		$this->pagination->initialize($config);		$this->_data['pagination']=$this->pagination->create_links();		$this->_data['template'] = 'admin/coupon/list_view';		$this->_data['title'] = 'Trang Quản Lý Mã Giảm Giá ';		$this->_data['info'] = $this->mcoupon->listAll($config['per_page'],$start);		$this->load->view("layout/admin",$this->_data);	}	public function add(){        $this->_data['action'] = strtolower(__FUNCTION__);		if(isset($_POST['ok'])){            $this->form_validation->set_rules("text","Chuỗi Mã","required");            $this->form_validation->set_rules("value","Giá Trị","required");            $this->form_validation->set_rules("start","Ngày Bắt Đầu","required");            $this->form_validation->set_rules("end","Ngày Kết Thúc","required");			if($this->form_validation->run()==TRUE){				$arr = array(					'type' => $this->input->post('type'),					'text' => $this->input->post('text'),					'value' => $this->input->post('value'),                    'start' => date("Y-m-d", strtotime($this->input->post('start'))),                    'end' => date("Y-m-d", strtotime($this->input->post('end')))					);				$this->mcoupon->add($arr);				$this->session->set_flashdata('ses_flash',"Thêm");				redirect(base_url()."admin/coupon/listall");			}		}		$this->_data['template'] = 'admin/coupon/modify_view';		$this->_data['title'] = 'Trang Thêm Thực Đơn ';		$this->load->view("layout/admin",$this->_data);	}	public function del($id){		$this->mcoupon->del($id);		$this->session->set_flashdata('ses_flash',"Xóa");		redirect(base_url()."admin/coupon/listall");	}	public function edit($id){        $this->_data['action'] = strtolower(__FUNCTION__);		if(isset($_POST['ok'])){            $this->form_validation->set_rules("text","Chuỗi Mã","required");            $this->form_validation->set_rules("value","Giá Trị","required");            $this->form_validation->set_rules("start","Ngày Bắt Đầu","required");            $this->form_validation->set_rules("end","Ngày Kết Thúc","required");            if($this->form_validation->run()==TRUE){                $arr = array(                    'type' => $this->input->post('type'),                    'text' => $this->input->post('text'),                    'value' => $this->input->post('value'),                    'start' => date("Y-m-d", strtotime($this->input->post('start'))),                    'end' => date("Y-m-d", strtotime($this->input->post('end'))),                );				$this->mcoupon->editById($id,$arr);				$this->session->set_flashdata('ses_flash',"Sửa");				redirect(base_url()."admin/coupon/listall");			}		}		$this->_data['info'] = $this->mcoupon->getById($id);		$this->_data['template'] = 'admin/coupon/modify_view';		$this->_data['title'] = 'Trang Sửa User ';		$this->load->view("layout/admin",$this->_data);	}}