'>                    <thead>                    <tr>                        <th></th>                        <th>Mã Đơn Hàng</th>                        <th>Email</th>                        <th>Ngày Đặt</th>                        <th>Chi Tiết </th>                        <th>Hình Thức Thanh Toán</th>                        <th>Tổng Tiền</th>                        <th>Mã Giảm Giá</th>                        <th>Tiền Phải Trả</th>                        <th>Tình Trạng</th>                    </tr>                    </thead>                    <tbody>                    <?php foreach($info as $val){ ?>                        <tr>                            <td>                                <div class="btn-group">                                    <button type="button" class="btn btn-default dropdown-toggle" data-toggle="dropdown">                                        <i class="fa fa-list"></i> <i class="fa fa-angle-down"></i>                                    </button>                                    <ul class="dropdown-menu">                                        <li><a href="<?php echo base_url()."admin/cart/detail/".$val['id']?>"><i class="fa fa-pencil"></i> Chi Tiết</a></li>                                        <li><a href="<?php echo base_url()."admin/cart/del/".$val['id']?>"  class="delButton"><i class="fa fa-trash-o"></i> Xóa</a></li>                                    </ul>                                </div>                            </td>                            <td><a href="<?php echo base_url()."admin/cart/detail/".$val['id']?>"><?php echo 'ORD-'.$val['id'] ?></a></td>                            <td><?php                                $sender = json_decode($val['sender'],true);                                echo $sender['user_email'];                                ?>                            </td>                            <td><?php echo date("d-m-Y",$val['thoigian']) ?></td>                            <td><?php                                if($val['detail'] == 'null'){                                    echo 'Liên Hệ' ;                                }else{                                    $this->load->model('mcategory');                                    $detail = json_decode($val['detail']) ;                                    foreach($detail as $v){                                        $pr = $this->mproduct->getByLink($v->name);                                        $cate = $this->mcategory->getById($pr['category_id']);                                        echo '<a target="_blank" href="'.base_url().$cate->link.'/'.$pr['link'].'">'.$pr['name'] ." x " .$v->qty ."</a> <br>";                                    }                                }                                ?></td>                            <td><?php echo $val['method'] ?></td>                            <td><?php echo number_format($val['total']).' VNĐ' ?></td>                            <td><?php echo $val['coupon'] ?></td>                            <td><?php echo number_format($val['total_after']).' VNĐ' ?></td>                            <td><?php                                switch ($val['status']) {                                    case 1:                                        echo "Đã Thanh Toán";                                        break;                                    case 2:                                        echo "Đang Chuyển Hàng";                                        break;                                    case 3:                                        echo "Hoàn Thành";                                        break;                                    case 4:                                        echo "Hủy Đơn Hàng";                                        break;                                    default:                                        echo "Đã Nhận";                                }                                ?>                            </td>                        </tr>                    <?php } ?>                    </tbody>                </table>                <div class="right" >                    <ul class="pagination">                        <?php echo $pagination ?>                    </ul>                </div>        </div>    </div></div>