<script>    $(document).ready(function(){        val = $('#parent_id').val();        if(val==0){            $('.avatar').css('display','none');                }        else{            $('.avatar').css('display','block');        }        $('#name').on('keyup change',function(){            var name = $('#name').val();            $.post( url + "admin/product/generateLink", { name : name})                .done(function(data ) {                    $('#link').val(data);                    $('#link_check').val(data+$('#parent_id').val());                    $('#full-link').val(url+'danh-muc/'+data);                });        })        $('#parent_id').change(function(){            val = $(this).val();            if(val==0){                $('.avatar').css('display','none');                    }            else{                $('.avatar').css('display','block');            }        })    })</script> <div class="portlet box blue">        <div class="portlet-title">            <div class="caption">                <i class="fa fa-reorder"></i><?php echo  (isset($info))?"Sửa ":"Thêm " ?>Danh Mục            </div>            <div class="tools">                <a href="javascript:;" class="collapse">                </a>                <a href="javascript:;" class="reload">                </a>            </div>        </div>        <div class="portlet-body form">            <!-- BEGIN FORM-->            <form action='<?php echo base_url()?>admin/category/<?php echo $action ?>/<?php echo  (isset($info))?$info->id:"" ?>' class="form-horizontal" method="post" enctype="multipart/form-data">                <div class="form-body">                    <?php if(validation_errors()!=''){ ?>                    <div class="note note-danger" >                            <button type="button" class="close" data-dismiss="alert" aria-hidden="true"></button>                            <?php echo validation_errors() ?>                        </div>                    <?php } ?>                    <div class="form-group">                        <label class="col-md-3 control-label">Danh Mục Cha</label>                        <div class="col-md-4">                            <select class="form-control" id="parent_id" name="parent_id">                                <option value="0">None</option>                                <?php foreach($listcate as $val){ ?>                                <option value="<?php echo $val->id ?>" <?php echo (isset($info)&&$info->parent_id==$val->id)?"selected":"" ?>><?php echo $val->name ?></option>                                <?php } ?>                            </select>                        </div>                    </div>                    <div class="form-group">                        <label class="col-md-3 control-label">Tên (*)</label>                        <div class="col-md-4">                            <input type="text" id='name' name='name' required="required" placeholder="Tên  ..." class='form-control' value='<?php                            echo (isset($info))?$info->name:"" ?>' />                        </div>                    </div>                    <div class="form-group">                        <label class="col-md-3 control-label">Đường Dẫn (*)</label>                        <div class="col-md-4">                            <input type="text" id='link' name='link2' required="required" placeholder="Đường Dẫn ..." class='form-control' value='<?php                            echo (isset($info))?$info->link:"" ?>' />                            <input type="hidden" name="link" id="link_check" />                        </div>                    </div>                    <?php if(isset($info) && $info->hinhanh!='error' && $info->hinhanh!=''){ ?>                    <div class="form-group">                        <label class="col-md-3 control-label">Hình ảnh (*)</label>                        <div class="col-md-4">                            <img src="<?php echo base_url() ?>public/danhmuc/<?php echo $info->hinhanh ?>" alt=" " />                        </div>                    </div>                    <?php } ?>                    <div class="form-group avatar">                        <label class="col-md-3 control-label">Hình Ảnh cũ ( 100 x 100 )</label>                        <div class="col-md-4">                            <input type="file" name="image" id="news-avatar" title="Tải Hình Ảnh" />                        </div>                    </div>                    <?php if(isset($info) && $info->hinhanh2!='error' && $info->hinhanh2!=''){ ?>                    <div class="form-group">                        <label class="col-md-3 control-label">Icon cũ (*)</label>                        <div class="col-md-4">                            <img src="<?php echo base_url() ?>public/danhmuc/<?php echo $info->hinhanh2 ?>" alt=" " />                        </div>                    </div>                    <?php } ?>                    <div class="form-group avatar">                        <label class="col-md-3 control-label">Icon ( 45 x 45 )</label>                        <div class="col-md-4">                            <input type="file" name="image2" id="news-avatar" title="Tải Hình Ảnh" class="file-input" />                        </div>                    </div>                    <div class="form-group">                        <label class="col-md-3 control-label">Sắp Xếp</label>                        <div class="col-md-4">                            <input type="text" id='order' name='order' placeholder="Thứ Tự Sắp Xếp ..." class='form-control' value='<?php                            echo (isset($info))?$info->order:"" ?>' />                        </div>                    </div>                </div>                <div class="form-actions nobg fluid">                    <div class="col-md-offset-3 col-md-9">                        <a href="<?php echo base_url() ?>admin/category/listall"  class="btn default">Hủy bỏ</a>                        <button class="btn green" id='btn-ok' name='ok' type='submit'>Xác Nhận</button>                    </div>                </div>            </form>            <!-- END FORM-->        </div>    </div>