<script>    $(document).ready(function(){        $('#name').on('keyup change',function(){            var name = $('#name').val();            $.post( url + "admin/product/generateLink", { name : name})                .done(function(data ) {                    $('#link').val(data);                });        })    })</script><div class="portlet box blue">        <div class="portlet-title">            <div class="caption">                <i class="fa fa-reorder"></i><?php echo  (isset($info))?"Sửa ":"Thêm " ?>Chất Liệu            </div>            <div class="tools">                <a href="javascript:;" class="collapse">                </a>                <a href="javascript:;" class="reload">                </a>            </div>        </div>        <div class="portlet-body form">            <!-- BEGIN FORM-->            <form action='<?php echo base_url()?>admin/supplier/<?php echo $action ?>/<?php echo  (isset($info))?$info['id']:"" ?>' class="form-horizontal" method="post" enctype="multipart/form-data">                <div class="form-body">                    <?php if(validation_errors()!=''){ ?>                    <div class="note note-danger" >                            <button type="button" class="close" data-dismiss="alert" aria-hidden="true"></button>                            <?php echo validation_errors() ?>                        </div>                    <?php } ?>                    <div class="form-group">                        <label class="col-md-3 control-label">Tên Chất Liệu (*)</label>                        <div class="col-md-4">                            <input type="text" id='name' name='name' required="required" placeholder="Tên Chất Liệu ..." class='form-control' value='<?php                            echo (isset($info))?$info['name']:"" ?>'>                        </div>                    </div>                    <div class="form-group">                        <label class="col-md-3 control-label">Link (*)</label>                        <div class="col-md-4">                            <input type="text" required="required" id='link' name='link' placeholder="Đường Dẫn ..." class='form-control' value='<?php                            echo (isset($info))?$info['link']:"" ?>'>                        </div>                    </div>                </div>                <div class="form-actions nobg fluid">                    <div class="col-md-offset-3 col-md-9">                        <a href="<?php echo base_url() ?>admin/supplier/listall"  class="btn default">Hủy bỏ</a>                        <button class="btn green" id='btn-ok' name='ok' type='submit'>Xác Nhận</button>                    </div>                </div>            </form>            <!-- END FORM-->        </div>    </div>